import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import axios from "axios";
import "./main.css";

function Header() {
    return (
        <header className="innerHeader">
            <img className="logo" src="/images/company-logo.jpg" alt="Company Logo" />
            <h1>Misal's BookShop</h1>
            <nav>
                <ul>
                    <li><Link to="/" className="active">Home</Link></li>
                    <li><Link to="/catalog">Catalog</Link></li>
                    <li><Link to="/cart">Cart</Link></li>
                </ul>
            </nav>
        </header>
    );
}

function Home() {
    return (
        <div className="home-container">
            <h1>Welcome to Misal's Bookstore</h1>
            <Link to="/catalog" className="button">Browse Catalog</Link>
        </div>
    );
}

function Catalog() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/catalog")
            .then((response) => setBooks(response.data))
            .catch((error) => console.error("Error fetching catalog:", error));
    }, []);

    const addToCart = (bookId) => {
        console.log("Adding book ID:", bookId);
        axios.post("http://localhost:8081/cart/add", { bookId })
            .then((response) => {
                console.log("Success:", response.data);
                alert("Added to cart!");
            })
            .catch((error) => console.error("Error details:", error.response || error.message));
    };

    return (
        <div className="container">
            <h1>Book Catalog</h1>
            <div className="book-list">
                {books.length === 0 ? (
                    <p>Loading books...</p>
                ) : (
                    books.map((book) => (
                        <section key={book.id} className="book-section">
                            <h2>{book.title}</h2>
                            <p>Price: ${book.price.toFixed(2)}</p>
                            <button onClick={() => addToCart(book.id)} className="button">
                                Add to Cart
                            </button>
                        </section>
                    ))
                )}
            </div>
        </div>
    );
}

function Cart() {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = () => {
        axios.get("http://localhost:8081/cart")
            .then((response) => setCartItems(response.data))
            .catch((error) => console.error("Error fetching cart:", error));
    };

    const removeFromCart = (id) => {
        axios.delete(`http://localhost:8081/cart/remove/${id}`)
            .then(() => fetchCart())
            .catch((error) => console.error("Error removing item:", error));
    };

    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <div className="cart-container">
            <h1>Shopping Cart</h1>
            {cartItems.length === 0 ? (
                <p>Your cart is empty. <Link to="/catalog">Browse books</Link></p>
            ) : (
                <>
                    <ul>
                        {cartItems.map((item) => (
                            <li key={item.id}>
                                {item.title} - ${item.price.toFixed(2)} x {item.quantity}
                                <button onClick={() => removeFromCart(item.id)} className="button remove-btn">
                                    Remove
                                </button>
                            </li>
                        ))}
                    </ul>
                    <p className="total">Total: ${total.toFixed(2)}</p>
                    <button className="button checkout-btn">Proceed to Checkout</button>
                </>
            )}
        </div>
    );
}

function Footer() {
    return (
        <footer>
            <small>Â© 2025 Misal Budha</small>
        </footer>
    );
}

export default function App() {
    return (
        <Router>
            <Header />
            <Routes>
                <Route path="/" element={<Home />} />
                <Route path="/catalog" element={<Catalog />} />
                <Route path="/cart" element={<Cart />} />
            </Routes>
            <Footer />
        </Router>
    );
}
