import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, NavLink, useNavigate } from "react-router-dom"; // Add useNavigate here
import axios from "axios";
import "./main.css";

function Header() {
    return (
        <header className="innerHeader">
            <img className="logo" src="/images/company-logo.png" alt="Company Logo" title="Company Logo" />
            <h1>Book Store</h1>
            <nav>
                <ul>
                    <li><NavLink to="/" className={({ isActive }) => (isActive ? "active" : "")}>Home</NavLink></li>
                    <li><NavLink to="/catalog" className={({ isActive }) => (isActive ? "active" : "")}>Catalog</NavLink></li>
                    <li><NavLink to="/cart" className={({ isActive }) => (isActive ? "active" : "")}>Cart</NavLink></li>
                </ul>
            </nav>
        </header>
    );
}

function Home() {
    return (
        <div className="home-container">
            <h1>Welcome to Bookstore</h1>
            <NavLink to="/catalog" className="button">Browse Catalog</NavLink>
        </div>
    );
}

function Catalog({ sessionId }) {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/catalog")
            .then((response) => {
                console.log("Catalog data:", response.data);
                setBooks(response.data);
            })
            .catch((error) => console.error("Error fetching catalog:", error));
    }, []);

    const addToCart = (bookId) => {
        if (!sessionId) {
            alert("Session not initialized. Please refresh the page.");
            return;
        }
        axios.post("http://localhost:8081/cart/add", { bookId }, { headers: { "Session-ID": sessionId } })
            .then(() => {
                alert("Added to cart!");
                axios.get("http://localhost:8081/cart", { headers: { "Session-ID": sessionId } })
                    .then((response) => window.dispatchEvent(new CustomEvent("cartUpdated", { detail: response.data })));
            })
            .catch((error) => console.error("Error adding to cart:", error.response?.data || error.message));
    };

    return (
        <div className="container">
            <h1>Book Catalog</h1>
            <div className="book-list">
                {books.length === 0 ? (
                    <p>Loading books...</p>
                ) : (
                    books.map((book) => (
                        <section key={book.id} className="book-section">
                            <h2>{book.title}</h2>
                            <img src={book.image_url} alt={book.title} width="100" onError={() => console.log(`Image failed: ${book.image_url}`)} />
                            <p>Price: ${Number(book.price).toFixed(2)}</p>
                            <p>Stock: {book.stock_quantity}</p>
                            <button onClick={() => addToCart(book.id)} className="button">Add to Cart</button>
                        </section>
                    ))
                )}
            </div>
        </div>
    );
}

function Cart({ sessionId }) {
    const [cartItems, setCartItems] = useState([]);
    const navigate = useNavigate(); // Now defined with the import

    useEffect(() => {
        fetchCart();
        const updateCart = (e) => setCartItems(e.detail);
        window.addEventListener("cartUpdated", updateCart);
        return () => window.removeEventListener("cartUpdated", updateCart);
    }, [sessionId]);

    const fetchCart = () => {
        if (!sessionId) return;
        axios.get("http://localhost:8081/cart", { headers: { "Session-ID": sessionId } })
            .then((response) => {
                console.log("Cart items:", response.data);
                setCartItems(response.data);
            })
            .catch((error) => console.error("Error fetching cart:", error));
    };

    const handleCheckout = () => {
        if (cartItems.length === 0) {
            alert("Your cart is empty!");
            return;
        }
        axios.post("http://localhost:8081/cart/order", { customerName: "Anonymous" }, { headers: { "Session-ID": sessionId } })
            .then((response) => {
                console.log("Order response:", response.data);
                alert("Order placed successfully!");
                setCartItems([]);
                fetchCart();
                navigate("/catalog"); // Works now
            })
            .catch((error) => {
                console.error("Order error:", error.response?.data || error.message);
                alert("Order failed: " + (error.response?.data || error.message));
            });
    };

    const removeFromCart = (id) => {
        if (!sessionId) {
            alert("Session not initialized. Please refresh the page.");
            return;
        }
        axios.delete(`http://localhost:8081/cart/${id}`, { headers: { "Session-ID": sessionId } })
            .then(() => {
                console.log("Item removed:", id);
                fetchCart();
            })
            .catch((error) => console.error("Error removing item:", error));
    };

    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <div className="container">
            <h1>Shopping Cart</h1>
            {cartItems.length === 0 ? (
                <p>Your cart is empty. <NavLink to="/catalog">Browse books</NavLink></p>
            ) : (
                <>
                    <ul>
                        {cartItems.map((item) => (
                            <li key={item.id}>
                                {item.title} - ${Number(item.price).toFixed(2)} x {item.quantity}
                                <button onClick={() => removeFromCart(item.id)} className="button remove-btn">Delete</button>
                            </li>
                        ))}
                    </ul>
                    <p className="total">Total: ${Number(total).toFixed(2)}</p>
                    <button onClick={handleCheckout} className="button checkout-btn">Checkout</button>
                </>
            )}
        </div>
    );
}

function Footer() {
    return (
        <footer>
            <small>Â© 2025 Book Store</small>
        </footer>
    );
}

export default function App() {
    const [sessionId, setSessionId] = useState(localStorage.getItem("sessionId") || null);
    const [sessionError, setSessionError] = useState(null);

    useEffect(() => {
        if (!sessionId) {
            axios.post("http://localhost:8081/cart/session")
                .then((res) => {
                    const newSessionId = res.data;
                    localStorage.setItem("sessionId", newSessionId);
                    setSessionId(newSessionId);
                    console.log("Session ID set:", newSessionId);
                })
                .catch((err) => {
                    const errorMsg = err.response
                        ? `Status: ${err.response.status}, Data: ${JSON.stringify(err.response.data)}`
                        : err.message;
                    console.error("Session error:", errorMsg);
                    setSessionError(`Failed to initialize session: ${errorMsg}. Please ensure cart-service is running and refresh.`);
                });
        }
    }, [sessionId]);

    if (!sessionId && sessionError) {
        return <div>{sessionError}</div>;
    }

    if (!sessionId) {
        return <div>Loading session...</div>;
    }

    return (
        <Router>
            <Header />
            <main>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/catalog" element={<Catalog sessionId={sessionId} />} />
                    <Route path="/cart" element={<Cart sessionId={sessionId} />} />
                </Routes>
            </main>
            <Footer />
        </Router>
    );
}
